dir = '/data1/DomainNet'
modes = ['train', 'test']
domains = ['clipart','painting','quickdraw','real','sketch']
cls_names = None

infs = {}
for domain in domains:
    infs[domain] = {}
    for mode in modes:
        infs[domain][mode] = []

        sdir = os.path.join(dir, domain, mode)
        if cls_names == None:
            cls_names = os.listdir(sdir)

        for cls_name in cls_names:
            cdir = os.path.join(sdir, cls_name)
            if not os.path.exists(cdir):
                os.makedirs(cdir)
                infs[domain][mode].append(0)
            else:
                cls_num = len(os.listdir(cdir))
                infs[domain][mode].append(cls_num)

for domain in domains:
    print('\n')
    print('###################################')
    print(domain)
    for mode in modes:
        print(mode)
        print(infs[domain][mode])
        #for cls_name in cls_names:
            #print(cls_name,'-', infs[domain][mode][cls_name])


